//Meshes
declare module "*.gltf" {
  const src: string;
  export default src;
}

//Images
declare module "*.webp" {
  const src: string;
  export default src;
}
declare module "*.png" {
  const src: string;
  export default src;
}
declare module "*.jpg" {
  const src: string;
  export default src;
}

//Audio
declare module "*.mp3" {
  const src: string;
  export default src;
}

//Misc display
declare module "*.svg" {
  const src: string;
  export default src;
}
